import { useState } from 'react';

import Button from './UI/Button';

const User = (props) => {
  const { onAddNewUser } = props;
  const [userName, setUserName] = useState('');
  const [userAge, setUserAge] = useState('');
  const [isValid, setIsValid] = useState(false);
  const [warnMessage, setWarnMessage] = useState('');

  const formSubmitHandler = (event) => {
    event.preventDefault();
    const newUser = {
      id: Math.random().toString(),
      name: userName,
      age: +userAge,
    };
    onAddNewUser(newUser);
    setUserName('');
    setUserAge('');
  };

  const userNameChangeHandler = ({ target }) => {
    setUserName(target.value);
  };

  const userAgeChangeHandler = ({ target }) => {
    setUserAge(target.value);
  }

  return (
    <form onSubmit={ formSubmitHandler }>
      <label htmlFor="userName">Username</label>
      <input name="userName" type="text" onChange={ userNameChangeHandler }/>
      <label htmlFor="userAge">Age (Years)</label>
      <input name="userAge" type="number" onChange={ userAgeChangeHandler }/>
      <Button type="submit">Add User</Button>
    </form>
  )
};

export default User;
