import { useState } from 'react';

import Card from '../UI/Card';
import Button from '../UI/Button';
import UsersList from './UsersList';

import styles from './AddUser.module.css';

const AddUser = (props) => {
  const { onAddNewUser } = props;
  const [userName, setUserName] = useState('');
  const [userAge, setUserAge] = useState('');
  const [isValid, setIsValid] = useState(false);
  const [warnMessage, setWarnMessage] = useState('');

  const addUserHandler = (event) => {
    event.preventDefault();
    if (userName.trim().length === 0 && userAge.trim().length === 0) {
      return;
    }
    if (+userAge < 1) {
      return;
    }
    const newUser = {
      id: Math.random().toString(),
      name: userName,
      age: +userAge,
    }
    onAddNewUser(newUser);
    setUserName('');
    setUserAge('');
    // if (!userName.trim().length) {
    //   setIsValid(false);
    //   setWarnMessage('Username is incorrect!');
    // }
    // if (+userAge && !userAge.includes('-')) {
    //   setIsValid(false);
    //   setWarnMessage('Age is incorrect!');
    // }
    // if (isValid) {
    //   const newUser = {
    //     id: Math.random().toString(),
    //     name: userName,
    //     age: +userAge,
    //   };
    //   onAddNewUser(newUser);
    //   setUserName('');
    //   setUserAge('');
    // }
  };

  const userNameChangeHandler = ({ target }) => {
    setUserName(target.value);
  };

  const userAgeChangeHandler = ({ target }) => {
    setUserAge(target.value);
  }

  return (
    <Card className={ styles.input }>
      <form onSubmit={ addUserHandler }>
        <label htmlFor="userName">Username</label>
        <input id="userName" type="text" value={ userName } onChange={ userNameChangeHandler }/>
        <label htmlFor="userAge">Age (Years)</label>
        <input id="userAge" type="number" value={ userAge } onChange={ userAgeChangeHandler }/>
        <Button type="submit" action={ addUserHandler }>Add User</Button>
      </form>
      <UsersList />
    </Card>
  )
};

export default AddUser;
